parameters.inputSchema:
{
    "type": "object",
    "properties": {
      "items": {
        "type": "array",
        "description": "An array of tasks",
        "items": {
          "type": "object",
          "properties": {
            "name": {
              "type": "string",
              "description": "The name of the task"
            },
            "description": {
              "type": "string",
              "description": "A detailed description of the task"
            },
            "due_date": {
              "type": "string",
              "description": "Due Date"
            },
            "priority": {
              "type": "string",
              "description": "Priority. . Please capitalize first letter"
            },
            "project_name": {
              "type": "string",
              "description": "Name of the project. Word 'Project' shouldn't be included"
            }
          },
          "required": [
            "name",
            "description",
            "due_date",
            "priority"
          ],
          "additionalProperties": false
        }
      }
    },
    "required": [
      "items"
    ],
    "additionalProperties": false
}

parameters.jsonBody:
={
  "query": "query Transcript($transcriptId: String!) { transcript(id: $transcriptId) { title participants speakers { id name } sentences { speaker_name text } summary { bullet_gist } } }",
  "variables": {
    "transcriptId": "{{ $json.meetingId }}"
  }
}
